sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var gaproduct = new GlideRecord('x_526619_mensaapp_produkte');
    gaproduct.addQuery("sys_id", "=", current.getValue("produkt"));
    gaproduct.query();

    if (gaproduct.next()) {
        var lager = gaproduct.getValue("lager") - current.getValue("menge");

        if (lager < "0") {
            gs.addErrorMessage("zu große Menge ausgewählt!");
            current.setAbortAction(true);
        } else {




            var price = gaproduct.getValue("preis");
            var ergebnislager = gaproduct.getValue("lager") - current.getValue("menge");
            gaproduct.setValue("lager", ergebnislager);
            gaproduct.update();
            var ergebnis = price * current.getValue("menge");

            current.endpreis = ergebnis;
        }


    }


})(current, previous);
